/* COMMON */
body {
  font-family: 'Nanum Gothic', sans-serif;
  color: #333;
  font-size: 16px;
  font-weight: 400;
  line-height: 1.4;
  height: 3000px;
}
img {
  display: block;
}
a {
  text-decoration: none;
}
.inner {
  width: 1100px;
  margin: 0 auto;
  position: relative;
}
// BUTTON
.btn {
  width: 130px;
  padding: 10px;
  border: 2px solid #333;
  border-radius: 4px;
  color: #333;
  font-size: 16px;
  font-weight: 700;
  text-align: center;
  cursor: pointer;
  box-sizing: border-box;
  display: block;
  transition: 0.4s;
  &:hover {
    background-color: #333;
    color: #fff;
  }
  &.btn--reverse {
    background-color: #333;
    color: #fff;
    &:hover {
      background-color: transparent;
      color: #333;
    }
  }
  &.btn--brown {
    color: #592b18;
    background-color: #fff;
    &:hover {
      color: #fff;
      background-color: #592b18;
    }
  }
  &.btn--gold {
    color: #d9aa8a;
    background-color: #fff;
    &:hover {
      color: #fff;
      background-color: #d9aa8a;
    }
  }
  &.btn--white {
    color: #fff;
    background-color: #fff;
    &:hover {
      color: #333;
      background-color: #fff;
    }
  }
}

// HEADER
header {
  width: 100%;
  background-color: #f6f5f0;
  border-bottom: 1px solid #c8c8c8;
  position: fixed;
  top: 0;
  z-index: 999;
  > .inner {
    height: 120px;
  }
  .logo {
    height: 75px;
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    margin: auto;
    // display: none;
  }
  .sub-menu {
    position: absolute;
    top: 10px;
    right: 0;
    display: flex;
    ul.menu {
      font-family: Arial, sans-serif;
      display: flex;
      li {
        position: relative;
        &::before {
          content: '';
          display: block;
          width: 1px;
          height: 12px;
          background-color: #e5e5e5;
          position: absolute;
          top: 0;
          bottom: 0;
          margin: auto;
        }
        &:first-child::before {
          display: none;
        }
        a {
          font-size: 12px;
          padding: 11px 16px;
          display: block;
          color: #656565;
          &:hover {
            color: #000;
          }
        }
      }
    }
    .search {
      height: 34px;
      position: relative;
      input {
        width: 36px;
        height: 34px;
        padding: 4px 10px;
        border: 1px solid #ccc;
        box-sizing: border-box;
        border-radius: 5px;
        outline: none;
        background-color: #fff;
        color: #777;
        transition: width 0.4s;
        font-size: 12px;
        &:focus {
          width: 190px;
          border-color: #669900;
        }
      }
      &.focused {
        .material-icons {
          opacity: 0;
        }
      }
      .material-icons {
        height: 24px;
        position: absolute;
        top: 0;
        bottom: 0;
        right: 5px;
        margin: auto;
        transition: 0.4s;
      }
    }
  }
  .main-menu {
    position: absolute;
    bottom: 0;
    right: 0;
    display: flex;
    z-index: 1;
    .item {
      .item__name {
        padding: 10px 20px 34px 20px;
        font-family: Arial, sans-serif;
        font-size: 13px;
      }
      .item__contents {
        width: 100%;
        position: fixed;
        left: 0;
        display: none;
        .contents__menu {
          background-color: #2c2a29;
          > ul {
            display: flex;
            padding: 20px 0;
            > li {
              width: 220px;
              h4 {
                padding: 3px 0 12px 0;
                font-size: 14px;
                color: #fff;
              }
              > ul {
                > li {
                  padding: 5px 0;
                  font-size: 12px;
                  color: #999;
                  cursor: pointer;
                  &:hover {
                    color: #669900;
                  }
                }
              }
            }
          }
        }
        .contents__texture {
          padding: 26px 0;
          font-size: 12px;
          background-image: url('../images/main_menu_pattern.jpg');
          .inner {
            > h4 {
              color: #999;
              font-weight: 700;
            }
            > p {
              color: #669900;
              margin: 4px 0 14px;
            }
          }
        }
      }
      &:hover {
        background-color: #2c2a29;
        color: #669900;
        border-radius: 6px 6px 0 0;
      }

      &:hover .item__contents {
        display: block;
      }
    }
  }
  .badges {
    position: absolute;
    top: 132px;
    right: 12px;
    .badge {
      border-radius: 10px;
      overflow: hidden;
      margin-bottom: 12px;
      box-shadow: 4px 4px 10px rgba(0, 0, 0, 0.15);
      cursor: pointer;
    }
  }
}

// VISUAL
.visual {
  margin-top: 120px;
  background-image: url('../images/visual_bg.jpg');
  > .inner {
    height: 646px;
    .title {
      position: absolute;
      top: 88px;
      left: -10px;
      .btn {
        position: absolute;
        top: 259px;
        left: 173px;
      }
    }
    .cup1 {
      &.image {
        position: absolute;
        bottom: 0;
        right: -47px;
      }
      &.text {
        position: absolute;
        top: 38px;
        right: 171px;
      }
    }
    .cup2 {
      &.image {
        position: absolute;
        bottom: 0;
        right: 162px;
      }
      &.text {
        position: absolute;
        top: 321px;
        right: 416px;
      }
    }
    > .spoon {
      position: absolute;
      bottom: 0;
      left: 275px;
    }
  }
  .fade_in {
    opacity: 0;
  }
}
// NOTICE
.notice {
  .notice-line {
    position: relative;
    .bg-left {
      position: absolute;
      top: 0;
      left: 0;
      width: 50%;
      height: 100%;
      background-color: #333;
    }
    .bg-right {
      position: absolute;
      top: 0;
      right: 0;
      width: 50%;
      height: 100%;
      background-color: #f6f5ef;
    }
    > .inner {
      height: 62px;
      display: flex;
      .inner__left {
        width: 60%;
        height: 100%;
        background-color: #333;
        display: flex;
        align-items: center;
        > h2 {
          color: #fff;
          font-size: 17px;
          font-weight: 700;
          margin-left: 20px;
        }
        > .swiper-container {
          height: 62px;
          flex-grow: 1;
          .swiper-slide {
            height: 62px;
            display: flex;
            align-items: center;
            justify-content: center;
            > a {
              color: #fff;
            }
          }
        }
        .notice-line__more {
          width: 62px;
          height: 62px;
          display: flex;
          justify-content: center;
          align-items: center;
          > .material-icons {
            color: #fff;
            font-size: 30px;
          }
        }
      }
      .inner__right {
        width: 40%;
        height: 100%;
        display: flex;
        justify-content: flex-end;
        align-items: center;
        > h2 {
          font-size: 17px;
          font-weight: 700;
        }
        .toggle-promotion {
          width: 62px;
          height: 62px;
          cursor: pointer;
          display: flex;
          justify-content: center;
          align-items: center;
          .material-icons {
            font-size: 30px;
          }
        }
      }
    }
  }
  .promotion {
    height: 693px;
    background-color: #f6f5ef;
    position: relative;
    transition: height 0.4s;
    overflow: hidden;
    &.hide {
      height: 0;
    }
    > .swiper-container {
      width: calc(819px * 3 + 20px);
      height: 553px;
      position: absolute;
      top: 40px;
      left: 50%;
      margin-left: calc((819px * 3 + 20px) / -2);
      .swiper-wrapper {
        .swiper-slide {
          opacity: 0.5;
          transition: opacity 1s;
          position: relative;
          .btn {
            position: absolute;
            bottom: 0;
            left: 0;
            right: 0;
            margin: auto;
          }
        }
        .swiper-slide-active {
          opacity: 1;
        }
      }
    }
    .swiper-pagination {
      bottom: 40px;
      left: 0;
      right: 0;
      .swiper-pagination-bullet {
        background-color: transparent;
        background-image: url('../images/promotion_slide_pager.png');
        width: 12px;
        height: 12px;
        margin-right: 6px;
        outline: none;
        &:last-child {
          margin-right: 0;
        }
      }
      .swiper-pagination-bullet-active {
